$('#pilihtanggal .choosedate').datepicker({
    'format':'d/m/yyyy',
    'autoclose':true,
});
/* end datepicker */



/* flex thumbnail */
var flex_thumb = function(){
	$(".flex_thumb").each(function(){
		$(this).find("img").css("opacity",0);
		var $source = $(this).find("img").attr("src");
		$(this).css({
		  'backgroundImage':'url(' + $source +')'
		});
	});
}
/* end flex thumbnail */



/* tabception */
jQuery('.tabception ul.tabs').each(function(){
    var $active, $content, $links = jQuery(this).find('a');
    $active = jQuery($links.filter('[href="'+location.hash+'"]')[0] || $links[0]);
    $active.addClass('active');
    $content = $($active[0].hash);

    $links.not($active).each(function () {
        jQuery(this.hash).hide();
    });

    jQuery(this).on('click', 'a', function(e){
        $active.removeClass('active');
        $content.hide();

        $active = jQuery(this);
        $content = jQuery(this.hash);

        $active.addClass('active');
        $content.show();
        e.preventDefault();
    });
});
/* tabception */



/* function afterload refresh */
var refresh_up = function(){
  var scroll = $(window).scrollTop();
  $("html").scrollTop(scroll);
  $('body').delay(1).animate({
    'scrollTop': $(window).scrollTop()-1
  },100);
}
/* end function afterload refresh */



/* sticky header */
var sticky_header = function(){
  $(window).resize(function(e){
    console.log(e);                   
  });

  /* header desktop */
  $(window).scroll(function (event) {
    var sc = $(window).scrollTop();
	var td = $('.topmenu-dummy').position().top;
	
	if(sc >= td){
		$('.topmenu').addClass("fixed");
	}
	else{
		$('.topmenu').removeClass("fixed");
	}
  });
  /* end header desktop */
}
/* end sticky header */



/* menu button */
var menu_mobile = function(){
  $(".top-mainmenu-button > a").click(function(){
    $(this).toggleClass("open");	
	$(this).parents().eq(1).find(".top-mainmenu").fadeToggle();
  });
}
/* end menu button */



/* main slideshow */
var main_slideshow = function(){
  $('.main-slideshow').slick({
	lazyLoad: 'ondemand',
	prevArrow:'<button type="button" data-role="none" class="slick-prev fa fa-chevron-circle-left">Previous</button>',
	nextArrow:'<button type="button" data-role="none" class="slick-next fa fa-chevron-circle-right">Next</button>',
    dots:false,
	infinite:true,
    slidesToShow:1,
	slidesToScroll:1,
	autoplay:true,
	arrows:true,
	autoplaySpeed:5000,
  });
}
/* end main slideshow */



$(document).ready(function(){
  flex_thumb();
  refresh_up();
  sticky_header();
  menu_mobile();
  main_slideshow();
});


$(".flex_ori > img").lazyload({effect:"fadeIn"});
$(".flex_lazy > img").lazyload();